import React, {useState} from 'react';

const InputElement = () => {
    
    const [inputText, setInputText] = useState("")

    return 
    <div>
        <input 
        onchange={(e) => {
            setInputText(e.target.value)
        }}
        placeholder="Enter some text"/>
    </div>
};

export default InputElement;
