import React, {useState} from 'react';

const InputElement = () => {
    
    const [inputText, setInputText] = useState("");
    const [historyList, sethistoryList] = useState([]);
    return( 
    <div>
        <input 
        onchange={(e) => {
            setInputText(e.target.value)
        }}
        placeholder="Enter some text"/><br/>
        {inputText}
    </div>
    )
};

export default InputElement;
